    function convertLength() {
      var lengthFrom = document.getElementById('lengthFrom').value;
      var lengthTo = document.getElementById('lengthTo').value;
      var lengthValue = document.getElementById('lengthValue').value;
      var result = document.getElementById('result');

      // Perform the conversion based on the selected units
      var convertedValue;
      if (lengthFrom === lengthTo) {
        convertedValue = lengthValue;
      } else if (lengthFrom === 'meter') {
        // Conversion from meter to other units
        switch (lengthTo) {
          case 'kilometer':
            convertedValue = lengthValue / 1000;
            break;
          case 'centimeter':
            convertedValue = lengthValue * 100;
            break;
          case 'millimeter':
            convertedValue = lengthValue * 1000;
            break;
          case 'micrometer':
            convertedValue = lengthValue * 1000000;
            break;
          case 'nanometer':
            convertedValue = lengthValue * 1000000000;
            break;
          case 'mile':
            convertedValue = lengthValue / 1609.34;
            break;
          case 'yard':
            convertedValue = lengthValue * 1.09361;
            break;
          case 'foot':
            convertedValue = lengthValue * 3.28084;
            break;
          case 'inch':
            convertedValue = lengthValue * 39.3701;
            break;
          case 'lightYear':
            convertedValue = lengthValue / 9.461e+15;
            break;
        }
      } else if (lengthTo === 'meter') {
        // Conversion to meter from other units
        switch (lengthFrom) {
          case 'kilometer':
            convertedValue = lengthValue * 1000;
            break;
          case 'centimeter':
            convertedValue = lengthValue / 100;
            break;
          case 'millimeter':
            convertedValue = lengthValue / 1000;
            break;
          case 'micrometer':
            convertedValue = lengthValue / 1000000;
            break;
          case 'nanometer':
            convertedValue = lengthValue / 1000000000;
            break;
          case 'mile':
            convertedValue = lengthValue * 1609.34;
            break;
          case 'yard':
            convertedValue = lengthValue / 1.09361;
            break;
          case 'foot':
            convertedValue = lengthValue / 3.28084;
            break;
          case 'inch':
            convertedValue = lengthValue / 39.3701;
            break;
          case 'lightYear':
            convertedValue = lengthValue * 9.461e+15;
            break;
        }
      }

      // Display the result
      result.innerHTML = lengthValue + ' ' + lengthFrom + ' = ' + convertedValue + ' ' + lengthTo;
    }

    function convertTemperature() {
      var tempFrom = document.getElementById('tempFrom').value;
      var tempTo = document.getElementById('tempTo').value;
      var tempValue = document.getElementById('tempValue').value;
      var result = document.getElementById('result');

      // Perform the conversion based on the selected units
      var convertedValue;
      if (tempFrom === tempTo) {
        convertedValue = tempValue;
      } else if (tempFrom === 'celsius') {
        // Conversion from Celsius to other units
        switch (tempTo) {
          case 'kelvin':
            convertedValue = parseFloat(tempValue) + 273.15;
            break;
          case 'fahrenheit':
            convertedValue = (parseFloat(tempValue) * 9 / 5) + 32;
            break;
        }
      } else if (tempTo === 'celsius') {
        // Conversion to Celsius from other units
        switch (tempFrom) {
          case 'kelvin':
            convertedValue = parseFloat(tempValue) - 273.15;
            break;
          case 'fahrenheit':
            convertedValue = (parseFloat(tempValue) - 32) * 5 / 9;
            break;
        }
      }

      // Display the result
      result.innerHTML = tempValue + ' ' + tempFrom + ' = ' + convertedValue + ' ' + tempTo;
    }

