    function convertWeight() {
      var weightFrom = document.getElementById('weightFrom').value;
      var weightTo = document.getElementById('weightTo').value;
      var weightValue = document.getElementById('weightValue').value;
      var result = document.getElementById('result');

      // Perform the conversion based on the selected units
      var convertedValue;
      if (weightFrom === weightTo) {
        convertedValue = weightValue;
      } else if (weightFrom === 'kilogram') {
        // Conversion from kilogram to other units
        switch (weightTo) {
          case 'gram':
            convertedValue = weightValue * 1000;
            break;
          case 'milligram':
            convertedValue = weightValue * 1e+6;
            break;
          case 'metricTon':
            convertedValue = weightValue / 1000;
            break;
          case 'longTon':
            convertedValue = weightValue / 1016.05;
            break;
          case 'shortTon':
            convertedValue = weightValue / 907.185;
            break;
          case 'pound':
            convertedValue = weightValue * 2.20462;
            break;
          case 'ounce':
            convertedValue = weightValue * 35.274;
            break;
          case 'carrat':
            convertedValue = weightValue * 5000;
            break;
          case 'atomicMassUnit':
            convertedValue = weightValue / 6.02214e+26;
            break;
        }
      } else if (weightTo === 'kilogram') {
        // Conversion to kilogram from other units
        switch (weightFrom) {
          case 'gram':
            convertedValue = weightValue / 1000;
            break;
          case 'milligram':
            convertedValue = weightValue / 1e+6;
            break;
          case 'metricTon':
            convertedValue = weightValue * 1000;
            break;
          case 'longTon':
            convertedValue = weightValue * 1016.05;
            break;
          case 'shortTon':
            convertedValue = weightValue * 907.185;
            break;
          case 'pound':
            convertedValue = weightValue / 2.20462;
            break;
          case 'ounce':
            convertedValue = weightValue / 35.274;
            break;
          case 'carrat':
            convertedValue = weightValue / 5000;
            break;
          case 'atomicMassUnit':
            convertedValue = weightValue * 6.02214e+26;
            break;
        }
      }

      // Display the result
      result.innerHTML = weightValue + ' ' + weightFrom + ' = ' + convertedValue + ' ' + weightTo;
    }

    function convertTime() {
      var timeFrom = document.getElementById('timeFrom').value;
      var timeTo = document.getElementById('timeTo').value;
      var timeValue = document.getElementById('timeValue').value;
      var result = document.getElementById('result');

      // Perform the conversion based on the selected units
      var convertedValue;
      if (timeFrom === timeTo) {
        convertedValue = timeValue;
      } else if (timeFrom === 'second') {
        // Conversion from second to other units
        switch (timeTo) {
          case 'millisecond':
            convertedValue = timeValue * 1000;
            break;
          case 'microsecond':
            convertedValue = timeValue * 1e+6;
            break;
          case 'nanosecond':
            convertedValue = timeValue * 1e+9;
            break;
          case 'picosecond':
            convertedValue = timeValue * 1e+12;
            break;
          case 'minute':
            convertedValue = timeValue / 60;
            break;
          case 'hour':
            convertedValue = timeValue / 3600;
            break;
          case 'day':
            convertedValue = timeValue / 86400;
            break;
          case 'week':
            convertedValue = timeValue / 604800;
            break;
          case 'month':
            convertedValue = timeValue / 2.628e+6;
            break;
          case 'year':
            convertedValue = timeValue / 3.154e+7;
            break;
        }
      } else if (timeTo === 'second') {
        // Conversion to second from other units
        switch (timeFrom) {
          case 'millisecond':
            convertedValue = timeValue / 1000;
            break;
          case 'microsecond':
            convertedValue = timeValue / 1e+6;
            break;
          case 'nanosecond':
            convertedValue = timeValue / 1e+9;
            break;
          case 'picosecond':
            convertedValue = timeValue / 1e+12;
            break;
          case 'minute':
            convertedValue = timeValue * 60;
            break;
          case 'hour':
            convertedValue = timeValue * 3600;
            break;
          case 'day':
            convertedValue = timeValue * 86400;
            break;
          case 'week':
            convertedValue = timeValue * 604800;
            break;
          case 'month':
            convertedValue = timeValue * 2.628e+6;
            break;
          case 'year':
            convertedValue = timeValue * 3.154e+7;
            break;
        }
      }

      // Display the result
      result.innerHTML = timeValue + ' ' + timeFrom + ' = ' + convertedValue + ' ' + timeTo;
    }
  </script>

